import { signup } from "../api/apiCalls";
import React, { Component } from "react";
import Input from "../../public/components/input";

export default class UserSignUp extends Component {
  state = {
    username: null,
    agreeCliked: false,
    displayName: null,
    password: null,
    passwordRepeat: null,
    pendingApiCall: false,
    errors: {},
  };
  onChangeHandler = (event) => {
    const { name, value } = event.target;
    const errors = { ...this.state.errors };
    errors[name] = undefined;
    this.setState({
      [name]: value,
      errors,
    });
  };

  onChangeAgree = (event) => {
    this.setState({
      agreeCliked: event.target.checked,
    });
    console.log(event.targe.value);
  };
  onClickSignUp = async (event) => {
    event.preventDefault();
    const { username, displayName, password } = this.state;
    //key value degeleri ayni ise js özelligi olarak sadece keyleri yazmak da yeterli
    const body = {
      username: username,
      displayName: displayName,
      password: password,
      // username,
      // displayName,
      // password,
    };
    this.setState({ pendingApiCall: true });

    // signup(body)
    //   .then((response) => {
    //     this.setState({ pendingApiCall: false });
    //   })
    //   .catch((error) => {
    //     this.setState({ pendingApiCall: false });
    //   });

    // api call bir baska yöntem ile js in yeni yöntemi ile yapacak olursak

    try {
      const response = await signup(body);
    } catch (error) {
      if (error.response.data.validationErrors) {
        this.setState({ errors: error.response.data.validationErrors });
      }
    }

    this.setState({ pendingApiCall: false });
  };
  render() {
    const { pendingApiCall, errors } = this.state;
    const { username, displayName } = errors;
    return (
      <div className="container">
        <form>
          <h1 className="text-center">Sign Up</h1>
          <Input name={username} />

          <div className="form-group">
            <label>Display Name</label>
            <input
              className={
                displayName ? "form-control is-invalid" : "form-control"
              }
              name="displayName"
              onChange={this.onChangeHandler}
            />
            <div className="invalid-feedback">{displayName}</div>
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              className="form-control"
              name="password"
              type="password"
              onChange={this.onChangeHandler}
            />
          </div>
          <div className="form-group">
            <label>Password Repeat</label>
            <input
              className="form-control"
              name="passwordRepeat"
              type="password"
              onChange={this.onChangeHandler}
            />
          </div>

          <div className="text-center form-group">
            {!pendingApiCall && (
              <button className="btn btn-primary" onClick={this.onClickSignUp}>
                Sign Up
              </button>
            )}
            {pendingApiCall && (
              <button className="btn btn-primary" disabled={true}>
                <span className="spinner-border spinner-border-sm"></span>
                Sign Up
              </button>
            )}
          </div>
        </form>
      </div>
    );
  }
}
