import React, { Component } from "react";

export default class UserSignUp extends Component {
  state = {
    username: null,
    agreeCliked: false,
    displayName: null,
    password: null,
    passwordRepeat: null,
  };
  onChangeHandler = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };
  onChangeUsername = (event) => {
    this.setState({
      username: event.target.value,
    });
    console.log(event.targe.value);
  };
  onChangeDisplayName = (event) => {
    this.setState({
      displayName: event.target.value,
    });
    console.log(event.targe.value);
  };
  onChangePassword = (event) => {
    this.setState({
      password: event.target.value,
    });
    console.log(event.targe.value);
  };
  onChangePasswordRepeat = (event) => {
    this.setState({
      passwordRepeat: event.target.value,
    });
    console.log(event.targe.value);
  };
  onChangeAgree = (event) => {
    this.setState({
      agreeCliked: event.target.checked,
    });
    console.log(event.targe.value);
  };
  render() {
    return (
      <form>
        <h1>Sign Up</h1>
        <div>
          <label>Username</label>
          <input name="username" onChange={this.onChangeUsername} />
        </div>
        <div>
          <label>Display Name</label>
          <input name="displayName" onChange={this.onChangeDisplayName} />
        </div>
        <div>
          <label>Password</label>
          <input
            name="password"
            type="password"
            onChange={this.onChangePassword}
          />
        </div>
        <div>
          <label>Password Repeat</label>
          <input name="passwordRepeat" type="password" />
        </div>
        <input type="checkbox" onChange={this.onChangeAgree} />
        {this.state.agreeCliked && <button>Sign Up</button>}
        {!this.state.agreeCliked && <button disabled="true">Sign Up</button>}
      </form>
    );
  }
}
