import axios from "axios";
import React, { Component } from "react";

export default class UserSignUp extends Component {
  state = {
    username: null,
    agreeCliked: false,
    displayName: null,
    password: null,
    passwordRepeat: null,
  };
  onChangeHandler = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  onChangeAgree = (event) => {
    this.setState({
      agreeCliked: event.target.checked,
    });
    console.log(event.targe.value);
  };
  onClickSignUp = (event) => {
    event.preventDefault();
    const { username, displayName, password } = this.state;
    const body = {
      // username: username,
      // displayName: displayName,
      // password: password,
      //key value degeleri ayni ise js özelligi olarak sadece keyleri yazmak da yeterli
      username,
      displayName,
      password,
    };

    axios.post("/api/1.0/users", body);
  };
  render() {
    return (
      <div className="container">
        <form>
          <h1 className="text-center">Sign Up</h1>
          <div>
            <label>Username</label>
            <input name="username" onChange={this.onChangeHandler} />
          </div>
          <div>
            <label>Display Name</label>
            <input name="displayName" onChange={this.onChangeHandler} />
          </div>
          <div>
            <label>Password</label>
            <input
              name="password"
              type="password"
              onChange={this.onChangeHandler}
            />
          </div>
          <div>
            <label>Password Repeat</label>
            <input
              name="passwordRepeat"
              type="password"
              onChange={this.onChangeHandler}
            />
          </div>
          <input type="checkbox" onChange={this.onChangeAgree} />
          {this.state.agreeCliked && (
            <button onClick={this.onClickSignUp}>Sign Up</button>
          )}
          {!this.state.agreeCliked && <button disabled="true">Sign Up</button>}
        </form>
      </div>
    );
  }
}
