import React, { Component } from "react";
import Input from "../components/Input";
import { withTranslation } from "react-i18next";
import { signup, changeLanguage } from "../api/apiCalls";

class LoginPage extends Component {
  state = {
    username: "",
    password: "",
  };

  onChangeHandler = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });

    console.log(this.state.username + " " + this.state.password);
  };
  onClickLogin = async (event) => {
    event.preventDefault();
    const { username, password } = this.state;
    const body = {
      username,
      password,
    };
    console.log(body);
  };
  onChangeLanguage = (language) => {
    const { i18n } = this.props;
    i18n.changeLanguage(language);
    changeLanguage(language);
  };

  render() {
    const { t } = this.props;
    return (
      <div className="container">
        <form>
          <h1 className="text-center">{t("Login")}</h1>
          <Input
            name="username"
            label={t("Username")}
            error={undefined}
            onChange={this.onChangeHandler}
          />
          <Input
            name="password"
            label={t("Password")}
            error={undefined}
            type="password"
            onChange={this.onChangeHandler}
          />
          <div className="text-center form-group mt-3">
            <button className="btn btn-primary" onClick={this.onClickLogin}>
              {t("Login")}
            </button>
          </div>
        </form>
        <div>
          <img
            onClick={() => this.onChangeLanguage("tr")}
            style={{ height: 20, width: 25, margin: 3, cursor: "pointer" }}
            src="https://countryflagsapi.com/png/tr"
            alt="TÃ¼rkce"
          ></img>
          <img
            onClick={() => this.onChangeLanguage("en")}
            style={{ height: 20, width: 25, margin: 3, cursor: "pointer" }}
            src="https://countryflagsapi.com/png/usa"
            alt="English"
          ></img>
        </div>
      </div>
    );
  }
}
const LoginPageWithTranslation = withTranslation()(LoginPage);
export default LoginPageWithTranslation;
